package sourceCode;
public class Addition 
{
	public static int add(int a,int b)
	{
		return a+b;  
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hello world");
		int a = 14;
		int b = 5;
		int answer;
		answer = add(a,b);
		System.out.println(answer);
	}

}
 